import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import HeatmapLayer from '../HeatmapLayer';
import { Tabs,Tab } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css'




 


class TabsMaps extends React.Component {
  constructor(props){
    super(props)
    

    this.state = {
      data:[],
      markers : [],
      flag:0,
      heatPoints:[]

    }
  }

  componentDidUpdate(prevProps) {
    if(prevProps.db!==this.props.db){
      this.setState({flag:0})
    }
    
  }

      
      render () {
          return(
        <>
           <Tabs defaultActiveKey="profile" id="uncontrolled-tab-example" className="mb-3">
             <Tab eventKey="home" title="Home">
               
             </Tab>
             <Tab eventKey="profile" title="Profile">
               
             </Tab>
             <Tab eventKey="contact" title="Contact" disabled>
               
             </Tab>
           </Tabs>
                      <div className="row">
                      <div className="col-lg-12">
                        <div className="chart" id="mapCanvas">
                        <Map center={[41.29246 ,13.5736108]} zoom={5} scrollWheelZoom={false}>
                        {this.state.markers.map((city, idx) => (
                        <Marker
                          position={[city.lat, city.lng]}
                          
                          key={idx}
                        >
                          <Popup>
                            <b>
                              {city.author}, {city.text}
                            </b>
                          </Popup>
                        </Marker>))}
                            <TileLayer
                                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
           
                        </Map>
                        </div>
                      </div>
           
                    </div>
                    <div className="row">
                      <div className="col-lg-12">
                        <div className="chart" id="mapCanvas">
                        <Map center={[41.29246 ,13.5736108]} zoom={5}>
                     <HeatmapLayer
                       points={this.state.heatPoints}
                       longitudeExtractor={m => m[1]}
                       latitudeExtractor={m => m[0]}
                       intensityExtractor={m => parseFloat(m[2])} />
                     <TileLayer
                       url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
                       attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                     />
                   </Map>
                        </div>
                      </div>
           
                    </div>
                    </>
                    )
      }

}
export default TabsMaps
