                  flag = true               
                }else{
                  flag = false
                }
                k++
              }

              if(flag===true){
                tempData[z]= this.state.data[i]
                z++
              }
              k=0
              j++
            }
          }
          i++
        }

       
        this.state.data=tempData
        this.state.totalTweets=tempData.length
        this.handleQuery()
        
      }

