import React from 'react';
import { TagCloud } from 'react-tagcloud'
import Filters from './Filters/WordChartFilters'
import PreLoader from "./preloader";

const customRenderer = (tag, size, color) => (
  <span
    key={tag.value}
    style={{
      animation: 'blinker 3s linear infinite',
      animationDelay: `${Math.random() * 2}s`,
      fontSize: `${size / 2}em`,
      border: `2px solid ${color}`,
      margin: '3px',
      padding: '3px',
      display: 'inline-block',
      color: 'white',
    }}
  >
    {tag.value}
  </span>
)

class WordCloud extends React.Component {
    constructor (props) {
        super(props)
        this.state = {
          data:[],
          words:[{
            text:null,
            value:null
          }],
          flag:0,
          flagWord:0

      }

    }
    componentDidUpdate(prevProps) {
      if(prevProps.db!==this.props.db){
        this.setState({flag:0})
      }
      
    }

handleQuery = (temp) => {

    
    this.setState({data:temp.data})
    this.state.data = temp.data
    this.state.flagWord = temp.typeWord;
    
    if(this.state.flagWord===0||this.state.flagWord==='0'){
      this.queryText();
    }else if(this.state.flagWord===1||this.state.flagWord==='1'){
      this.queryTag();
    }else{
      this.queryHashtag();
    }
    


  }

  queryTag = () =>{

    var i = 0
    var j = 0
    var k = 0
   
    var words=[{
        value:null,
        count:0
    }]

    var temp = null

    var arrayWords = []

    var flag = false

    
    
    while(i<this.state.data.length){
      j=0;
      if(this.state.data[i].tags.tag_me!==undefined){
        
        while(j<this.state.data[i].tags.tag_me.length){
          
          temp=this.state.data[i].tags.tag_me[j].split(' : ')
          
          while(k<arrayWords.length){
            if(arrayWords[k]===temp[0]){
              flag=true
              break
            }
            k++
          }
    
          if(flag===true){
            words[k].value++
          }else{
            arrayWords.push(temp[0])
            words.push({
              value:temp[0],
              count:1
            })            
      
           k++;
            j++;
          }    
      
      
        }
      }

    i++
    }
    
    
    this.state.words=words
    this.setState({words:words})
    
    this.setState({flag:1})
    

  }

  queryHashtag = () =>{

    var i = 0
    var j = 0
    var k = 0
   
    var words=[{
        value:null,
        count:0
    }]

    var temp = null;

    var arrayWords = [];

    var flag = false;

    

    
     
    while(i<this.state.data.length){
      j=0;

      if(this.state.data[i].twitter_entities.hashtags!==undefined){
        
        while(j<this.state.data[i].twitter_entities.hashtags.length){
          
          temp=this.state.data[i].twitter_entities.hashtags[j]
          
          while(k<arrayWords.length){
            if(arrayWords[k]===temp){
              flag=true
              break
            }
            k++
          }
    
          if(flag===true){
            words[k].count++
          }else{
            arrayWords.push(temp)
            words.push({
              value:temp,
              count:1
            })            
      
           k++;
            j++;
          }    
      
      
        }
      }
     
     i++
     
    }
    console.log(arrayWords)
    this.state.words=words
    this.setState({words:words})
    
    this.setState({flag:1})

  }


 
      queryText = () =>{

        var i = 0
        var j = 0
        var k = 0
       
        var words=[{
          count:null,
            count:0
        }]

        var temp = null

        var arrayWords = []

        var flag = false

        

       
        while(i<this.state.data.length){
          j=0;
         
          if(this.state.data[i].spacy!==undefined){
            while(j<this.state.data[i].spacy.processed_text.length){
              temp=this.state.data[i].spacy.processed_text[j].split(" ")[0]
            
             if(this.checkWord(temp)===false&&this.state.data[i].spacy.processed_text[j].split(" ")[3]!=='CCONJ'
              ){
                k=0
              flag = false
              while(k<arrayWords.length){
                if(arrayWords[k]===temp){
                  flag=true
                  break
                }
                k++
              }
              if(flag===true){
                words[k].count++
              }else{
                arrayWords.push(temp)
  
                words.push({
                  value:temp,
                  count:1
                })
                  
              }
              }
              
   
                
              j++
            }
          }

         i++
         
        }
        this.state.words=words
        this.setState({words:words})
        
        this.setState({flag:1})

      }
   
      checkWord = (temp) => {

        //check if string is a tag 
        if(temp[0]==='@'){
          return true
        }
    
        //check if string is a number
        if(!isNaN(+temp)){
          return true
        }
        
        //check if string is a url

        //controllo url rimosso per migliorare le
        /*
        var pattern = new RegExp('^(https?:\\/\\/)?'+ // protocol
          '((([a-z\\d]([a-z\\d-]*[a-z\\d])*)\\.)+[a-z]{2,}|'+ // domain name
          '((\\d{1,3}\\.){3}\\d{1,3}))'+ // OR ip (v4) address
          '(\\:\\d+)?(\\/[-a-z\\d%_.~+]*)*'+ // port and path
          '(\\?[;&a-z\\d%_.~+=-]*)?'+ // query string
          '(\\#[-a-z\\d_]*)?$','i'); // fragment locator
  
        if(pattern.test(temp)){
          return true
        }
        */

        return false
        
      }
    
      render () {
        var body;
        if(this.state.flag>0){
          body =<div className="row">
          <div className="col-lg-12">
            <div className="chart" id="wordChart">
            <TagCloud
    minSize={12}
    maxSize={35}
    tags={this.state.words}
    renderer={customRenderer}
   
  />
            </div>
          </div>

        </div>
        }else{
          body=
          <div className="row">
            <div className="col-lg-12">
            <div className="chart"> <PreLoader/></div>
          </div>
          </div>
        }
          return(
        <div className="main-wrapper">
        {/* ! Main */}
        <main className="main users chart-page" id="skip-target">
          <div className="container">
            <h1>CrowdPulse</h1>
            <br/>
            <h3>Word Cloud - {this.props.mongodb} </h3>
            <br/>
            <Filters parentCallback = {this.handleQuery.bind(this)} db = {this.props.db}  tweetsData={this.props.allTweetsData}/>
            <br/>

            {body}
          </div>
        </main>
        {/* ! Footer */}
        <footer className="footer" style={{ background: 'blue' }}>
          <div className="container footer--flex">
            <div className="footer-start">
              <p>2021 Â© Giovanni Tempesta </p>
            </div>
          </div>
        </footer>
      </div>
      )
      }

}
export default WordCloud
