import React from 'react'
import ReactTags from 'react-tag-autocomplete'
import './css/searchbar.css';
import axios from 'axios';


//https://www.npmjs.com/package/react-tag-autocomplete
class SearchFilters extends React.Component {
  constructor (props) {
    super(props)
    this.sendData = this.sendData.bind(this)
    this.state = {
      tags: [     
      ],
      suggestions: [
       
      ]
    }
  

    axios.get('/tweet/getTags', {
      params: {
        db: this.props.db
      }
    })
        .then((response) => {
          var i = 0;
          var j = 0;
          var k =0;
          const data = response.data;
             
          var temp ;
          var tempSuggestion = [];
          var strings = [];


          while(i<data[i]._id.length){
            j=0;
            
            if(data[i]._id!==undefined&&data[i]._id!==null){
              
              while(j<data[i]._id.tag_me.length){
                
                temp=data[i]._id.tag_me[j].split(" : ")

                if(strings.indexOf(temp[0])>-1){

                }else{
                  tempSuggestion[k] = {
                    id:temp[1],
                    name: temp[0]
                  };
                  strings.push(temp[0])
                }


                k++;
                j++;
              }
            }
              i++;  
          }
          
          
          //this.state.suggestions = tempSuggestion
          this.setState({suggestions: tempSuggestion})
         
           
      })
      .catch((error) => {
          console.log('error: ', error)
      });
    
      

    this.reactTags = React.createRef()
  }


  componentDidUpdate(prevProps) {
    if(prevProps.db!==this.props.db){
      axios.get('/tweet/getTags', {
        params: {
          db: this.props.db
        }
      })
          .then((response) => {
            var i = 0;
            var j = 0;
            var k =0;
            const data = response.data;
               
            var temp ;
            var tempSuggestion = [];
            var strings = [];
            
            while(i<data.length){
              j=0;
              if(data[i]._id!==undefined&&data[i]._id!==null){
                while(j<data[i]._id.tag_me.length){
                  temp=data[i]._id.tag_me[j].split(" : ")
                  
                  if(strings.indexOf(temp[0])==-1){
                    tempSuggestion[k] = {
                      id:temp[1],
                      name: temp[0]
                    }
                    strings.push(temp[0]);
                  }
  
                  k++;
                  j++;
                }
              }
                i++;
            }
            
            
            //this.state.suggestions = tempSuggestion
            this.setState({suggestions: tempSuggestion})
           
             
        })
        .catch((error) => {
            console.log('error: ', error)
        });
      
        
  
      this.reactTags = React.createRef()
    }
    
  }
  

  sendData = (tags) =>{
    this.props.parentCallback(tags);
  }

  onDelete (i) {
    const tags = this.state.tags.slice(0)
    tags.splice(i, 1)
    this.setState({ tags })
    this.sendData(tags)
  }

  onAddition (tag) {
    const tags = [].concat(this.state.tags, tag)
    this.setState({ tags })
    this.sendData(tags)
  }

  render () {
    return (
     
      <ReactTags
        ref={this.reactTags}
        tags={this.state.tags}
        suggestions={this.state.suggestions}
        onDelete={this.onDelete.bind(this)}
        onAddition={this.onAddition.bind(this)} 
        classNames="search"
        />
     

    )
  }
}


export default SearchFilters
